import nc from "next-connect";
import notes from "../../../src/data/data";

// getServerSideProps will get the data upon every request (not at build time)

const handler = nc()
  // CREATING A NOTE
  .post((req, res) => {
    const note = {
      ...req.body,
      id: Date.now()
    };
    notes.push(note);
    res.json({ data: note });
  })
  // GET ALL NOTES
  .get((req, res) => {
    res.json({ data: notes });
  });

export default handler;
